CREATE TABLE IF NOT EXISTS Games(
    GAME_ID INT AUTO_INCREMENT PRIMARY KEY,
    GAME_NAME VARCHAR(255) DEFAULT NULL,
    RENTED BIT DEFAULT 0,
    RELEASE_YEAR YEAR DEFAULT NULL,
    RATING FLOAT DEFAULT NULL,
    PLATFORM VARCHAR(255) DEFAULT NULL,
    GENRE VARCHAR(255) DEFAULT NULL,
    MEDIA_TYPE VARCHAR(255) DEFAULT NULL,
    GAME_DESCRIPTION TEXT DEFAULT NULL,
    REVIEW_SOURCE TEXT DEFAULT NULL,
    IMAGE_LINK TEXT DEFAULT NULL,
	OUTLET VARCHAR(255) DEFAULT NULL
);






CREATE TABLE IF NOT EXISTS Members(
    MEMBER_ID INT AUTO_INCREMENT PRIMARY KEY,
    FIRST_NAME VARCHAR(255) DEFAULT NULL,
    LAST_NAME VARCHAR(255) DEFAULT NULL,
    DOB DATE DEFAULT NULL,
    MEMBER_EMAIL VARCHAR(255) UNIQUE,
    PHONE VARCHAR(255) DEFAULT NULL,
    MEMBER_TYPE VARCHAR(255) DEFAULT NULL,
	HASHED_PASS VARCHAR(255) DEFAULT NULL
);



CREATE TABLE IF NOT EXISTS Rentals(
    RENT_ID INT AUTO_INCREMENT PRIMARY KEY,
    DATE_RENTED DATE DEFAULT NULL,
    DUE_DATE DATE DEFAULT NULL,
    DATE_RETURNED DATE DEFAULT NULL,
    EXTRA_EXTENSIONS INT DEFAULT 0,
    GAME_ID INT NOT NULL,
    MEMBER_ID INT NOT NULL,
    FOREIGN KEY(GAME_ID) REFERENCES Games(GAME_ID) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY(MEMBER_ID) REFERENCES Members(MEMBER_ID) ON UPDATE CASCADE ON DELETE CASCADE
);


CREATE TABLE IF NOT EXISTS Bans(
    REPORT_ID INT AUTO_INCREMENT PRIMARY KEY,
    GAME_ID INT NOT NULL,
    MEMBER_ID INT NOT NULL,
    FEE INT DEFAULT 0,
    REFUNDED INT DEFAULT 0,
    INFO TEXT DEFAULT NULL,
	UNBAN_DATE VARCHAR(255) DEFAULT NULL,
    FOREIGN KEY (GAME_ID) REFERENCES Games(GAME_ID) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (MEMBER_ID) REFERENCES Members(MEMBER_ID) ON UPDATE CASCADE ON DELETE CASCADE
);


CREATE TABLE IF NOT EXISTS Rules(
    RULE INT AUTO_INCREMENT PRIMARY KEY,
    RENT_PERIOD INT DEFAULT 3,
    EXTENSIONS_PER_RENT INT DEFAULT 2,
    EXTENSIONS_PERIOD INT DEFAULT 1,
    TOTAL_RENTS INT DEFAULT 2,
    BAN_PERIOD INT DEFAULT 6,
    NUMBER_VIOLATION INT DEFAULT 3,
    NUMBER_VIOLATION_PERIOD INT DEFAULT 12
);

