CREATE TABLE IF NOT EXISTS Violations(
	VIOLATION_ID INT AUTO_INCREMENT PRIMARY KEY,
	MEMBER_ID INT NOT NULL,
	NUMBER_OF_VIOLATION INT DEFAULT 1,
	INI_VIOLATION_DATE DATE NOT NULL,
	FOREIGN KEY (MEMBER_ID) REFERENCES Members(MEMBER_ID) ON UPDATE RESTRICT ON DELETE CASCADE
);